const buttons=document.querySelectorAll(".footer__btn");buttons.forEach(e=>{e.addEventListener("click",()=>{e.classList.contains("selected")?e.classList.remove("selected"):(buttons.forEach(e=>e.classList.remove("selected")),e.classList.add("selected"))})});const mobileBtn=document.querySelector(".header__btn-menu"),menu=document.querySelector(".menu"),closeBtn=document.querySelector(".close");mobileBtn.addEventListener("click",()=>{menu.classList.toggle("active")}),closeBtn.addEventListener("click",()=>{menu.classList.toggle("active")});